 .btn::before {
   content: '';
   position: absolute;
   width: 20px;
   height: 20px;
   top: 50%;
   left: 0;
   transform: translate(0, -50%);
   border-radius: inherit;
   transition: left .2s ease-in-out, width .1s linear, height .1s linear;
   z-index: -1;
   pointer-events: none;
 }

 .go_top {
   position: fixed;
   bottom: 20px;
   /* bottom: 160px; */
   right: 20px;
   text-align: center;
   line-height: 60px;
   opacity: 0;
   visibility: hidden;
   transition: all 0.3s;
   cursor: pointer;
   -webkit-animation: bb_bounce 1.5s infinite;
   -moz-animation: bb_bounce 1.5s infinite;
   -ms-animation: bb_bounce 1.5s infinite;
   animation: bb_bounce 1.5s infinite;
 }


 .vote-team td {
