 body .news-container {
   position: fixed;
   top: 0;
   background: #fff;
   z-index: 99;
   left: 0;
   right: 0;
   font-family: "Roboto", sans-serif;
   box-shadow: 0 4px 8px -4px rgba(0, 0, 0, 0.3);
 }

 .news-container .title {
   position: absolute;
   background: #fff;
   height: 100%;
   display: flex;
   align-items: center;
   padding: 0 24px;
   color: white;
   font-weight: bold;
   z-index: 200;
 }

 .news-container ul {
   display: flex;
   list-style: none;
   margin: 0;
   margin-top: 10px;
   margin-bottom: 10px;
   line-height: 1.4;

   /* Using shorthand notation */
   animation: scroll 35s infinite linear;

   /* Using longhand notation with iteration count */
   animation-name: scroll;
   animation-duration: 35s;
   animation-timing-function: linear;
   animation-iteration-count: infinite;
 }

 .news-container ul li {
   white-space: nowrap;
   padding: 10px 24px;
   color: #494949;
   position: relative;
   min-width: 150px;
 }

 .news-container ul li span {
   padding: 6px 12px;
 }

 /* .news-container ul li::after {
  content:"";
  width: 1px;
  height: 100%;
  background: #b8b8b8;
  position: absolute;
  top: 0;
  right: 0;
} */

 .news-container ul li:last-child::after {
   display: none;
 }

 @keyframes scroll {
   from {
     transform: translateX(80%);
   }

   to {
     transform: translateX(-1083px);

   }
 }

 .popup {
   position: fixed;
   top: 0;
   left: 0;
   width: 100%;
   height: 100%;
   background: rgba(0, 0, 0, 0.5);
   display: flex;
   align-items: center;
   justify-content: center;
   z-index: 99;
 }

 .popup-content {
   position: relative;
   min-width: 480px;
   background: #fff;
   padding: 25px;
   text-align: center;
   border-radius: 5px;
   box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
 }

 .popup-content img {
   width: auto;
   max-height: 200px;
 }

 .page-logo img {
   max-height: 66px;
 }

 .popup-content .title {}

 .popup-content input {
   text-align: center;
   color: #000;
   width: 100%;
   padding: 10px;
   margin: 10px 0;
   box-sizing: border-box;
   border: 1px solid #ccc;
   border-radius: 5px;
 }

 .popup-content .btn.close {
   position: absolute;
   top: 5px;
   right: 5px;
 }

 .btn.submit {
   color: #ffffff !important;
   background-color: #478ac9 !important;

   text-transform: uppercase;
   font-weight: 700;
 }

 .message {
   position: relative;
   margin-top: 10px;
   margin-bottom: 10px;
   /* left: 50%; */
   /* transform: translateX(-50%); */
   padding: 10px;
   border-radius: 5px;
   color: #fff;
   font-weight: bold;
   z-index: 1000;
   opacity: 1;
   transition: opacity 0.5s ease-in-out;
 }

 .success {
   background-color: #4caf50;
   /* Green background */
 }

 .error {
   background-color: #f44336;
